package day06

fun main() {

    val sizePart1 = 4
    val sizePart2 = 14

    val example1 = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"
    val example2 = "bvwbjplbgvbhsrlpgdmjqwftvncz"
    val example3 = "nppdvjthqldpwncqszvftbrmjlhg"
    val example4 = "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"
    val example5 = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"
    val input =
        ""

    println(getFirstMarker(example1, sizePart1) == 7)
    println(getFirstMarker(example2, sizePart1) == 5)
    println(getFirstMarker(example3, sizePart1) == 6)
    println(getFirstMarker(example4, sizePart1) == 10)
    println(getFirstMarker(example5, sizePart1) == 11)
    println(getFirstMarker(input, sizePart1) == 1093)

    println(getFirstMarker(example1, sizePart2) == 19)
    println(getFirstMarker(example2, sizePart2) == 23)
    println(getFirstMarker(example3, sizePart2) == 23)
    println(getFirstMarker(example4, sizePart2) == 29)
    println(getFirstMarker(example5, sizePart2) == 26)
    println(getFirstMarker(input, sizePart2) == 3534)

}

private fun getFirstMarker(input: String, size: Int): Int {
    return input.indexOf(
        input.windowed(size).first {
            it.chars().distinct().toArray().size == size
        }
    ) + size
}