package day06

fun main() {

    val size = 4

    println(getFirstMarker("mjqjpqmgbljsphdztnvjfqwrcgsmlb", size) == 7)
    println(getFirstMarker("bvwbjplbgvbhsrlpgdmjqwftvncz", size) == 5)
    println(getFirstMarker("nppdvjthqldpwncqszvftbrmjlhg", size) == 6)
    println(getFirstMarker("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg", size) == 10)
    println(getFirstMarker("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw", size) == 11)

    println(
        getFirstMarker(
            "",
            size
        ) == 1093
    )

}

private fun getFirstMarker(input: String, size: Int): Int {
    return input.indexOf(
        input.windowed(size).first {
            it.chars().distinct().toArray().size == size
        }
    ) + size
}